import React from "react";

class ButtonLabelCounter extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            counter:0,
        };
    }
    render() {
        return (
            <div>
            <button onClick={() => this.setState({counter: this.state.counter+1})}> Click me</button>
            <p> Hai cliccato {this.state.counter} volte </p>
            </div>
            );

    }
}

export default ButtonLabelCounter;
