import React, { useState } from "react";
import { Collapse, Navbar, NavbarToggler, Nav, NavItem } from "reactstrap";
import { NavLink as RouterLink } from "react-router-dom";
import style from "./Header.module.css";

const Header = (props) => {
    const {logo, navItems} =props;

    const [isOpen, setIsOpen] = useState(false);

    const toggle = () => setIsOpen(!isOpen);

    const itemsList = navItems.map((item) => {
        return (
            <NavItem className={style.navItem} key={item.url}>
                <RouterLink
                    exact={item.exact}
                    activeclassName={style.active}
                    to={item.url}
                    className="nav-link"

                >
                    {navItem.text}
                </RouterLink>
            </NavItem>
        );
    });

    return (
        <div>
            <Navbar fixed="top" light expand="md" className={style.navBar}>
                <RouterLink className={"ms-md-5"} to="/">
                    <img src={props.logo} className={""} alt="logo" />
                </RouterLink>
                <NavbarToggler onClick={toggle} />
                <Collapse className={"justify-content-end me-5"} isOpen={isOpen} navbar >
                    <Nav className="ml-auto" navbar>
                        {navItemsList}
                    </Nav>
                </Collapse>
            </Navbar>
        </div>
    );
};

export default Header;
