import React from "react";
import { Tooltip } from "reactstrap";
import {useParams} from "react-router-dom/cjs/react-router-dom";
import style from "./ProductType.module.css";


const ProductType = (props) => {
    const { data } = props;

    const params = useParams();
    const id = parseInt(params.number);
    const currentProduct = data.filter((product) => product.id === id) [0];
    let colorPills;
    if(currentProduct){
         colorPills = currentProduct.product_colors.map((color, index)=>{
            return (
                <>
                <span id={index} className={style.pill} style={{backgroundColor: color.hex_value}}>
                    {" "}
                </span>
                <Tooltip
                    flip
                    target={index}
                    toggle={function noRefCheck(){}}
                >
                    Hello world!
                </Tooltip>
                </>

            )
        });
    }


    return (
        <div className="container">
            <div className="row">
                <div className="col">
                    {currentProduct &&
                    (
                        <>
                            <h1> {currentProduct.name}</h1>
                            <img src={currentProduct["image_link"]}/>
                            <p> {currentProduct.description}</p>
                            <p> {currentProduct.price}$</p>
                            <a className="btn btn-primary" href={currentProduct.product_link}>Acquista</a>
                            <div className="mt-4"> {colorPills} </div>

                        </>
                    )}
                </div>

            </div>
        </div>
    )}

export default ProductType;
