import React, {useState} from "react";
import ProductTable from "../../components/ProductTable/ProductTable";
import ProductCardsGrid from "../../components/ProductCardsGrid/ProductCardsGrid";
import style from "./Makeup.module.css";
import clsx from "clsx";
import {useParams} from "react-router-dom";

function Makeup () {
    const [displayGrid, setDisplayGrid] = useState("true");
    const params=useParams();
    const category=params.string;
    const [makeups,setMakeup] = useState([]);
    useEffect(()=>{
        fetch("http://makeup-api.herokuapp.com/api/v1/products.json?product_type="+category).then((data)=> {
            data.json().then((json)=>{
                setMakeup(json);
            })

        })

    });
    console.log(makeups)
    const listMakeup = makeups.map((makeup,index) => {
        return (<p>{makeup.name}</p>);
    });
    return (
        <div className="container">
            <div className="row justify-content-center">
                <div className="col">
                    <div className={style.switch}>
                    <div className={clsx(style.option, {[style.active]: displayGrid})}>
                        <div onClick={()=> setDisplayGrid(true)}> Grid
                        </div>
                    </div>
                    <div className={clsx(style.option, {[style.active]: displayGrid})}>
                        <div onClick={()=> setDisplayGrid(false)}> Table
                        </div>
                    </div>
                </div>
                </div>
            </div>

            <div className="row justify-content-center">
                <div className="col">
                    {displayGrid ? <ProductCardsGrid/> : <ProductTable/>}
                </div>
            </div>
        </div>
    )
}

export default Makeup;
