import React from "react";
import ProductCard from "../ProductCard/ProductCard";

function ProductCardsGrid(props) {
    const { data, col } = props;

    const cardCols = data.map((products, index) => {
        return (
            <div className="col" key={index} className="pb-3">
                <ProductCard
                    id={products?.id}
                    name={products.name}
                    product_type={products.product_type}
                    image_link={products.image_link}
                />
            </div>
        );
    });

    return (
        <div
            className={`row
        row-cols-${col.xs}
        row-cols-sm-${col.sm}
        row-cols-md-${col.md}
        row-cols-lg-${col.lg}
        row-cols-xl-${col.xl}
        `}
        >
            {cardCols}
        </div>
    );
}

export default ProductCardsGrid;
