import React from "react";
import {useParams} from "react-router-dom/cjs/react-router-dom";


const ProductType = (props) => {
    const { data } = props;

    const params = useParams();
    const id = parseInt(params.number);
    const currentProduct = data.filter((product) => product.id === id) [0];

    if(currentProduct){
        const colorPills = currentProduct.product_colors.map((color)=>{
            return (
                <div className={"text-black"}>
                    ciao
                    {color.colour_name}
                </div>
            )
        });
    }


    return (
        <div className="container">
            <div className="row">
                <div className="col">
                    {currentProduct &&
                    (
                        <>
                            <h1> {currentProduct.name}</h1>
                            <img src={currentProduct["image_link"]}/>
                            <p> {currentProduct.description}</p>
                            <p> {currentProduct.price}$</p>
                            <a className="btn btn-primary" href={currentProduct.product_link}>Acquista</a>

                            {colorPills}

                        </>
                    )}
                </div>

            </div>
        </div>
    )}

export default ProductType;
